import React, { useState, useEffect } from "react";
import { Amplify } from "aws-amplify";
import Authentication from "./components/Authentication";
import HomePage from "./components/HomePage";
import SessionSetup from "./components/SessionSetup/SessionSetup";
import SessionPage from "./components/SessionPage"; // Add this import
import authService from "./services/authService";
import awsConfig from "./aws-config";
Amplify.configure(awsConfig);

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);
  const [currentView, setCurrentView] = useState("home");
  const [sessionData, setSessionData] = useState(null); // Add session data state

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    setIsLoading(true);
    try {
      const result = await authService.getCurrentUser();
      if (result.success && result.user) {
        setCurrentUser(result.user);
        setIsAuthenticated(true);
      } else {
        setIsAuthenticated(false);
        setCurrentUser(null);
      }
    } catch (error) {
      console.log("No authenticated user found");
      setIsAuthenticated(false);
      setCurrentUser(null);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAuthSuccess = () => {
    checkAuthStatus();
  };

  const handleSignOut = () => {
    setIsAuthenticated(false);
    setCurrentUser(null);
    setCurrentView("home");
    setSessionData(null); // Clear session data on sign out
  };

  const handleStartSession = () => {
    setCurrentView("sessionSetup");
  };

  const handleBackToDashboard = () => {
    setCurrentView("home");
  };

  const handleStartInvestigation = (sessionData) => {
    console.log("Starting investigation with:", sessionData);

    // Store the session data and navigate to SessionPage
    setSessionData(sessionData);
    setCurrentView("session");
  };

  // Add handler to go back to home from session page
  const handleEndSession = () => {
    setCurrentView("home");
    setSessionData(null);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-indigo-600 mx-auto mb-4"></div>
          <p className="text-gray-600 text-lg">Loading Vision AI...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      {!isAuthenticated ? (
        <Authentication onAuthSuccess={handleAuthSuccess} />
      ) : currentView === "sessionSetup" ? (
        <SessionSetup
          onBackToDashboard={handleBackToDashboard}
          onStartInvestigation={handleStartInvestigation}
        />
      ) : currentView === "session" ? ( // Add SessionPage view
        <SessionPage
          user={currentUser}
          onSignOut={handleSignOut}
          sessionData={sessionData}
          onEndSession={handleEndSession} // Add this prop
        />
      ) : (
        <HomePage
          user={currentUser}
          onSignOut={handleSignOut}
          onStartSession={handleStartSession}
        />
      )}
    </div>
  );
}

export default App;
